public class NZCPJNI {

    private String jti;
    private String iss;
    private int nbf;
    private int exp;
    private String given_name;
    private String family_name;
    private String dob;

    static {
        System.load(System.getProperty("user.dir") + "/libnzcpjni.so"); // TODO: proper path concat
    }
    public native int verify_pass_uri(String pass_uri);

    public static void main(String[] args) {
        NZCPJNI nzcp = new NZCPJNI();
        int error = nzcp.verify_pass_uri("NZCP:");
        System.out.printf("error %d\n", error);
    }
}
